mapper.select:
select
  TITLE, DESC, GMT_CREATE, GMT_MODIFIED, COMMENT_COUNT,  view_count, LIKE_COUNT, TAG, CREATOR
from PUBLIC.QUESTION


        and TITLE = #{title}


        and DESC = #{desc}


        and GMT_CREATE = #{gmtCreate}


        and GMT_MODIFIED = #{gmtModified}


        and COMMENT_COUNT = #{commentCount}


        and  view_count = #{ viewCount}


        and LIKE_COUNT = #{likeCount}


        and TAG = #{tag}


        and CREATOR = #{creator}
